import React, { useState } from 'react';
import { Card, CardContent, CardHeader, CardTitle } from '../ui/card';

interface BudgetFormProps {
  onSave: (budget: { category: string; amount: number; period: 'monthly' | 'annual' }) => void;
  onCancel: () => void;
}

const BudgetForm: React.FC<BudgetFormProps> = ({ onSave, onCancel }) => {
  const [budget, setBudget] = useState({
    category: '',
    amount: '',
    period: 'monthly' as 'monthly' | 'annual'
  });

  const handleSubmit = (e: React.FormEvent) => {
    e.preventDefault();
    onSave({
      category: budget.category,
      amount: parseFloat(budget.amount),
      period: budget.period
    });
    setBudget({ category: '', amount: '', period: 'monthly' });
  };

  return (
    <Card>
      <CardHeader>
        <CardTitle>Add New Budget Category</CardTitle>
      </CardHeader>
      <CardContent>
        <form onSubmit={handleSubmit} className="space-y-4">
          <div>
            <label className="block text-sm font-medium mb-1">Category Name</label>
            <input
              type="text"
              value={budget.category}
              onChange={(e) => setBudget({ ...budget, category: e.target.value })}
              className="w-full p-2 border rounded"
              required
            />
          </div>
          <div>
            <label className="block text-sm font-medium mb-1">Budget Amount</label>
            <input
              type="number"
              value={budget.amount}
              onChange={(e) => setBudget({ ...budget, amount: e.target.value })}
              className="w-full p-2 border rounded"
              required
            />
          </div>
          <div>
            <label className="block text-sm font-medium mb-1">Period</label>
            <select
              value={budget.period}
              onChange={(e) => setBudget({ ...budget, period: e.target.value as 'monthly' | 'annual' })}
              className="w-full p-2 border rounded"
            >
              <option value="monthly">Monthly</option>
              <option value="annual">Annual</option>
            </select>
          </div>
          <div className="flex justify-end gap-2">
            <button
              type="button"
              onClick={onCancel}
              className="px-4 py-2 border rounded hover:bg-gray-100"
            >
              Cancel
            </button>
            <button
              type="submit"
              className="px-4 py-2 bg-indigo-500 text-white rounded hover:bg-indigo-600"
            >
              Add Budget Category
            </button>
          </div>
        </form>
      </CardContent>
    </Card>
  );
};

export default BudgetForm;
