import React, { useEffect, useState } from 'react';
import { BrowserRouter as Router, Routes, Route } from 'react-router-dom';
import BudgetTracker from './components/BudgetTracker';
import Navigation from './components/Navigation';
import AccountDetails from './pages/AccountDetails';
import { accountsApi, transactionsApi, AccountData, TransactionData } from './services/api';

const App: React.FC = () => {
  const [accounts, setAccounts] = useState<AccountData[]>([]);
  const [transactions, setTransactions] = useState<TransactionData[]>([]);

  useEffect(() => {
    const loadData = async () => {
      try {
        const [accountsData, transactionsData] = await Promise.all([
          accountsApi.getAll(),
          transactionsApi.getAll()
        ]);
        setAccounts(accountsData);
        setTransactions(transactionsData);
      } catch (error) {
        console.error('Failed to load data:', error);
      }
    };

    loadData();
  }, []);

  return (
    <Router>
      <div className="min-h-screen bg-gray-100">
        <Navigation accounts={accounts} />
        <main className="pt-6">
          <Routes>
            <Route path="/" element={<BudgetTracker />} />
            <Route 
              path="/accounts/:id" 
              element={<AccountDetails accounts={accounts} transactions={transactions} />} 
            />
          </Routes>
        </main>
      </div>
    </Router>
  );
};

export default App;
