import React from 'react';
import { Link, useLocation } from 'react-router-dom';
import { AccountData } from '../services/api';

interface NavigationProps {
  accounts: AccountData[];
}

const Navigation: React.FC<NavigationProps> = ({ accounts }) => {
  const location = useLocation();

  return (
    <nav className="bg-gray-800 text-white p-4">
      <div className="max-w-5xl mx-auto">
        <div className="space-y-4">
          <Link 
            to="/" 
            className={`block py-2 px-4 rounded ${location.pathname === '/' ? 'bg-gray-700' : 'hover:bg-gray-700'}`}
          >
            Budget Overview
          </Link>

          <div className="space-y-2">
            <div className="text-gray-400 text-sm font-semibold px-4">Accounts</div>
            {accounts.map(account => (
              <Link
                key={account.id}
                to={`/accounts/${account.id}`}
                className={`block py-2 px-4 rounded ${
                  location.pathname === `/accounts/${account.id}` ? 'bg-gray-700' : 'hover:bg-gray-700'
                }`}
              >
                <div className="flex justify-between items-center">
                  <span>{account.name}</span>
                  <span className={`text-sm ${account.current_balance >= 0 ? 'text-green-400' : 'text-red-400'}`}>
                    ${account.current_balance.toFixed(2)}
                  </span>
                </div>
              </Link>
            ))}
          </div>
        </div>
      </div>
    </nav>
  );
};

export default Navigation;
