import React, { useState } from 'react';
import { Card, CardContent, CardHeader, CardTitle } from './ui/card';
import { BarChart, Bar, XAxis, YAxis, ResponsiveContainer, Tooltip } from 'recharts';
import { Plus, ChevronLeft, ChevronRight } from 'lucide-react';
import { Tabs, TabsContent, TabsList, TabsTrigger } from './ui/tabs';
import BudgetForm from './budget/BudgetForm';
import TransactionForm from './transactions/TransactionForm';

interface Transaction {
  id: number;
  category: string;
  amount: number;
  date: string;
  description: string;  // Added this field
}

interface Budgets {
  monthly: Record<string, number>;
  annual: Record<string, number>;
}

const BudgetTracker: React.FC = () => {
  const [currentMonth, setCurrentMonth] = useState(new Date());
  const [transactions, setTransactions] = useState<Transaction[]>([
    { id: 1, category: 'Groceries', amount: 450, date: '2025-02-10', description: 'Monthly groceries' },
    { id: 2, category: 'Utilities', amount: 120, date: '2025-02-05', description: 'Electricity bill' },
    { id: 3, category: 'Entertainment', amount: 80, date: '2025-02-15', description: 'Movie night' },
    { id: 4, category: 'Transport', amount: 100, date: '2025-02-08', description: 'Bus pass' },
    { id: 5, category: 'Travel', amount: 1200, date: '2025-02-20', description: 'Flight tickets' },
    { id: 6, category: 'Insurance', amount: 800, date: '2025-02-01', description: 'Car insurance' }
  ]);

  const [budgets, setBudgets] = useState<Budgets>({
    monthly: {
      Groceries: 500,
      Utilities: 150,
      Entertainment: 200,
      Transport: 150
    },
    annual: {
      Travel: 5000,
      Insurance: 2400
    }
  });

  const [showBudgetForm, setShowBudgetForm] = useState(false);
  const [showTransactionForm, setShowTransactionForm] = useState(false);
  const [editingTransaction, setEditingTransaction] = useState<Transaction | null>(null);

  const handleAddBudget = (newBudget: { category: string; amount: number; period: 'monthly' | 'annual' }) => {
    setBudgets(prev => ({
      ...prev,
      [newBudget.period]: {
        ...prev[newBudget.period],
        [newBudget.category]: newBudget.amount
      }
    }));
    setShowBudgetForm(false);
  };

  const handleAddTransaction = (newTransaction: Omit<Transaction, 'id'>) => {
    setTransactions(prev => [...prev, { ...newTransaction, id: Date.now() }]);
    setShowTransactionForm(false);
  };

  const handleEditTransaction = (transaction: Omit<Transaction, 'id'>) => {
    if (editingTransaction) {
      setTransactions(prev =>
        prev.map(t =>
          t.id === editingTransaction.id ? { ...transaction, id: t.id } : t
        )
      );
      setEditingTransaction(null);
    }
    setShowTransactionForm(false);
  };

  return (
    <div className="max-w-5xl mx-auto p-4 space-y-6">
      <div className="flex justify-between items-center">
        <h1 className="text-2xl font-bold text-gray-900">Budget Tracker</h1>
        <button
          onClick={() => setShowTransactionForm(true)}
          className="flex items-center gap-2 bg-indigo-500 text-white px-4 py-2 rounded-lg hover:bg-indigo-600 transition-colors"
        >
          <Plus size={20} /> Add Transaction
        </button>
      </div>

      <button
        onClick={() => setShowBudgetForm(true)}
        className="flex items-center gap-2 bg-green-500 text-white px-4 py-2 rounded-lg hover:bg-green-600 transition-colors"
      >
        <Plus size={20} /> Add Budget Category
      </button>

      <Card className="border-none shadow-sm">
        <CardContent>
          <div className="flex justify-between items-center py-4">
            <button className="p-2 hover:bg-gray-100 rounded-full transition-colors">
              <ChevronLeft className="text-gray-600" />
            </button>
            <h2 className="text-xl font-semibold text-gray-800">
              {currentMonth.toLocaleString('default', { month: 'long', year: 'numeric' })}
            </h2>
            <button className="p-2 hover:bg-gray-100 rounded-full transition-colors">
              <ChevronRight className="text-gray-600" />
            </button>
          </div>
        </CardContent>
      </Card>

      <Tabs defaultValue="monthly" className="space-y-6">
        <TabsList className="w-full justify-start border-b mb-4">
          <TabsTrigger value="monthly" className="px-6">Monthly Budgets</TabsTrigger>
          <TabsTrigger value="annual" className="px-6">Annual Budgets</TabsTrigger>
        </TabsList>

        <TabsContent value="monthly">
          <div className="grid grid-cols-1 lg:grid-cols-2 gap-6">
            <Card className="border-none shadow-sm">
              <CardHeader>
                <CardTitle>Monthly Progress</CardTitle>
              </CardHeader>
              <CardContent>
                {Object.entries(budgets.monthly).map(([category, budget]) => (
                  <div key={category} className="mb-4">
                    <div className="flex justify-between mb-2">
                      <span>{category}</span>
                      <span>${budget}</span>
                    </div>
                    <div className="h-2 bg-gray-200 rounded-full">
                      <div
                        className="h-full bg-indigo-500 rounded-full"
                        style={{ width: `${(transactions.find(t => t.category === category)?.amount || 0) / budget * 100}%` }}
                      />
                    </div>
                  </div>
                ))}
              </CardContent>
            </Card>
          </div>
        </TabsContent>

        <TabsContent value="annual">
          <div className="grid grid-cols-1 lg:grid-cols-2 gap-6">
            <Card className="border-none shadow-sm">
              <CardHeader>
                <CardTitle>Annual Progress</CardTitle>
              </CardHeader>
              <CardContent>
                {Object.entries(budgets.annual).map(([category, budget]) => (
                  <div key={category} className="mb-4">
                    <div className="flex justify-between mb-2">
                      <span>{category}</span>
                      <span>${budget}</span>
                    </div>
                    <div className="h-2 bg-gray-200 rounded-full">
                      <div
                        className="h-full bg-indigo-500 rounded-full"
                        style={{ width: `${(transactions.find(t => t.category === category)?.amount || 0) / budget * 100}%` }}
                      />
                    </div>
                  </div>
                ))}
              </CardContent>
            </Card>
          </div>
        </TabsContent>
      </Tabs>

      {showBudgetForm && (
        <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center p-4">
          <div className="max-w-md w-full">
            <BudgetForm onSave={handleAddBudget} />
          </div>
        </div>
      )}

      {showTransactionForm && (
        <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center p-4">
          <div className="max-w-md w-full">
            <TransactionForm
              categories={[
                ...Object.keys(budgets.monthly),
                ...Object.keys(budgets.annual)
              ]}
              onSave={editingTransaction ? handleEditTransaction : handleAddTransaction}
              editTransaction={editingTransaction || undefined}
            />
          </div>
        </div>
      )}
    </div>
  );
};

export default BudgetTracker;
